SCTX:
Begin aeslipScript

float timer
short doOnce
short index

if ( OnDeath == 1 )
	if ( AeslipTalk == 0 )
		Journal "BM_Draugr" 90
	elseif ( AeslipTalk > 0 )
		if ( GetJournalIndex BM_Draugr < 80 )
			Journal BM_Draugr 90
		endif
	endif
endif

if ( doOnce > 0 )
	return
endif

if ( GetJournalIndex BM_Draugr >= 40 )
	if ( GetJournalIndex BM_Draugr <= 50 )
		if ( AeslipTalk == 0 )
			if ( GetDistance Player <= 512 )
				ForceGreeting
			endif
		endif
	endif
endif

if ( GetJournalIndex "BM_Draugr" == 80 )
	set timer to ( timer + GetSecondsPassed )
	if ( timer >= 3 )
		ForceGreeting
		set doOnce to 1
	endif
endif 

End aeslipScript
