Begin ashamanuScript

; Ashamanu will activate on Journal Index 20

if ( GetDisabled != 1 )
	if ( GetJournalIndex "MS_WhiteGuar" >= 70 )
		Disable
	endif
endif

if ( GetDisabled == 1 )
	if ( GetJournalIndex MS_WhiteGuar >= 20 )
		Enable
	endif
endif

; Ashamanu will give journal entry 60 when player is near

if ( GetDisabled != 1 )
	if ( GetDistance Player <= 256 )
		if ( GetDistance "guar_white_unique" <= 256 )
			if ( GetJournalIndex "MS_WhiteGuar" <= 50 )
				Journal "MS_WhiteGuar" 60
			endif
		endif
	endif
endif



if ( GetDisabled != 1 )
	if ( GetDistance Player <= 256 )
		if ( GetJournalIndex "MS_WhiteGuar" <= 50 )
			Journal "MS_WhiteGuar" 65
		endif
	endif
endif


End
