Begin cariushuntScript

short doOnce
float timer
short nolore
short playerwolf
short AllowWerewolfForceGreeting

if ( GetJournalIndex BM_WildHunt < 30 )
	return
endif

if ( OnDeath == 1 )
	Journal BM_WildHunt 49 
endif

if ( GetJournalIndex BM_WildHunt == 30 )
	if ( player->IsWerewolf == 0 )
		if ( GetDistance Player <= 512 )
			if ( doOnce == 0 )
			set timer to ( timer + GetSecondsPassed )
				if ( timer > 1.5 )
					ForceGreeting
					set doOnce to 1
				endif
			endif
		endif
	elseif ( player->IsWerewolf == 1 )
		;StartCombat Player
		if ( GetDistance Player <= 512 )
			if ( doOnce == 0 )
			set timer to ( timer + GetSecondsPassed )
				if ( timer > 1.5 )
					set playerwolf to 1
					ForceGreeting
					set doOnce to -1
				endif
			endif
		endif
	endif
endif

if ( doOnce == 1 )
	if ( Player->IsWerewolf == 0 )
		if ( GetCurrentAiPackage == 3 )
			if ( GetDistance active_BM_carius <= 512 )
				if ( Player->GetDistance BM_mazegate_01 <= 800 )
					if ( GetDistance Player <= 512 )
						set cariustalk to 1
						AiWander 0 0 0 0 0 0
						ForceGreeting
					endif
				endif
			endif
		endif
	endif
endif



End cariushuntScript
