Begin Colony_E_2_M

;enables mid-stage buildings for Stage 2 of colony development
;objects are disabled when Stage 2 is completed

short done

if ( GetDisabled == 0 )
	if ( ColonyState < 3 )
		Disable
	endif
endif

if ( done == 0 )
	if ( GetDisabled == 1 )
		if ( ColonyState >= 3 )
			Enable
			set done to 1
		endif
	endif
endif

if ( GetDisabled == 0 )
	if ( ColonyState >= 10 )
		Disable
	endif
endif

End
