Begin Colony_E_4_M

;enables mid-stage buildings for Stage 4 of colony development
;objects are disabled when Stage 4 is completed

short done

if ( GetDisabled == 0 )
	if ( ColonyState < 22 )
		Disable
	endif
endif

if ( done == 0 )
	if ( GetDisabled == 1 )
		if ( ColonyState >= 22 )
			Enable
			set done to 1
		endif
	endif
endif

if ( GetDisabled == 0 )
	if ( ColonyState >= 30 )
		Disable
	endif
endif

End
