Begin ColonyBaro

short nolore
short weaponout
short saberout
short questdone

if ( GetJournalIndex CO_5 >= 70 )
	if ( GetJournalIndex CO_5 < 200 )
		set questdone to 1
	endif
endif

if ( Ondeath == 1 )
	Journal CO_5 200
endif


if ( MenuMode == 1 )
	return
endif

if ( GetDisabled == 0 )
	if ( ColonyService < 5 )
		Disable
	endif
endif

if ( questdone == 0 )
	if ( GetDisabled == 1 )
		if ( ColonyService >= 5 )
			Enable
		endif
	endif
endif

if ( questdone == 1 )
	if ( GetDisabled == 0 )
		Disable
	endif
endif

if ( GetDistance Player < 512 )
	if ( player->GetWeaponDrawn == 1 )
		if (  player->HasItemEquipped "steel saber_elberoth" == 1 )
			set saberout to 1
		else
			if ( player->GetWeaponType == -1 )
				set weaponout to 1
			else set weaponout to 2
			endif
		endif
	else
		set saberout to 0
	endif
endif

End ColonyBaro
