Begin ColonyMineDoor

;enables door and controls entry to mine during quest CO_2


if ( GetDisabled == 0 )
	if ( ColonyState < 1 )
		Disable
	endif
endif

if ( GetDisabled == 1 )
	if ( ColonyState >= 2 )
		Enable
	endif
endif

if ( GetJournalIndex CO_2 >= 10 )
	if ( GetJournalIndex CO_2 < 50 )
		if ( GetDistance "Hroldar the Strange" <= 256 )
			If ( OnActivate == 1 )
				MessageBox "The door cannot be opened. Hroldar is blocking the way."
			endif
		endif
	endif
endif

if ( GetJournalIndex CO_2 >= 50 )
	if ( OnActivate == 1 )
		Activate
	endif
endif

End ColonyDoorMine
