Begin ColonyServSmithConst

;enables Smithy construction piece if it is chosen in quest CO_5


if ( ColonyService < 3 )
	if ( GetDisabled == 0 )
		Disable
	endif
endif

if ( ColonyService == 3 )
	if ( GetDisabled == 1 )
		Enable
	endif
endif

if ( ColonyService >= 5 )
	if ( GetDisabled == 0 )
		Disable
		StopScript ColonyServTraderConst
	endif
endif

End ColonyServSmithConst
