Begin ColonyShipwreck

short doonce




if ( GetDisabled == 0 )
	if ( GetJournalIndex "CO_3" < 10 )
		Disable
	endif
endif

if ( GetDisabled == 1 )
	if ( GetJournalIndex "CO_3" >= 10 )
		Enable
	endif
endif

if ( doonce == 0 )
	if ( GetJournalIndex "CO_3" >= 30 )
		if ( GetDistance Player <= 2500 )
			Journal CO_3 50
			set doonce to 1
		endif
	endif
endif

End ColonyShipwreck
