Begin ColonyUnel

;controls enabling of Unel Lloran for quest CO_10, and his behavior throughout the quest.


short nolore


if ( GetJournalIndex "CO_10" < 10 )
	if ( GetDisabled == 0 )
		Disable
	endif
endif

if ( GetJournalIndex "CO_10" >= 10 )
	if ( GetDisabled == 1 )
		Enable
	endif
endif




if ( GetJournalIndex "CO_10" == 60 )
	if ( GetCurrentAiPackage == 3 )
		if ( GetDistance "act_ex_colony_root_4" <= 1000 )
			Journal CO_10 70
			AiWander  0 0 0 0 0 0
			ForceGreeting
		endif
	endif
endif

End ColonyUnel
