Begin ColonyWolf_Hroldar

short doonce
short wolfattack

if ( GetDisabled == 0 )
	if ( GetJournalIndex "CO_9a" < 40 )
		Disable
	endif
endif

if ( GetDisabled == 1 )
	if ( GetJournalIndex "CO_9a" >= 40 )
		if ( GetDeadCount "Hroldar the Strange_b" < 1 )
			Enable
		endif
	endif
endif

if ( doonce == 0 )
	if ( GetDistance "Tirvel Balen" <= 512 )
		StartCombat "Tirvel Balen"
		set doonce to 1
		set wolfattack to 1
	endif
endif

if ( doonce == 1 )
	if ( GetDeadCount "Tirvel Balen" >= 1 )
		StopCombat
		SetFight 30
		set doonce to 2
	endif
endif

End ColonyWolf_Hroldar
