SCTX:
begin DagothUrCreature2

short state
float timer
short teleportDisabled

if ( teleportDisabled == 0 )
	DisableTeleporting
	Set teleportDisabled to 1
endif

if ( Menumode == 1 )
	return
endif

;return if heart is destroyed, so he can be killed
if ( HeartDestroyed == 1 )
	if ( GetHealth > 200 )
		SetHealth 200
	endif
	if ( GetMagicka > 0 )
		SetMagicka 0
	endif
	return
endif

;keep alive
if ( GetHealth < 1000 )
	SetHealth 1000		
endif

;infinite spells
if ( GetMagicka < 100 )
	SetMagicka 1000
endif

if  ( state == 0 )

	if ( timer < 1 )
		Set timer to ( timer + GetSecondsPassed )
		return
	endif

	Set timer to 0

	;play initial greeting, once
	Player->Say, "Vo\Misc\Dagoth Ur Welcome C.wav", "What a fool you are. I'm a god. How can you kill a god? What a grand and intoxicating innocence. How could you be so naive? There is no escape. No Recall or Intervention can work in this place. Come. Lay down your weapons. It is not too late for my mercy."
	set state to 10
endif

if ( "heart_akulakhan".countHits == 0 )

	;if Dagoth has not been hit or it is reset, reset dagoth's shields

	if ( GetSpell "dagoth_ur_shield_01" == 0 )
		AddSpell "dagoth_ur_shield_01"
	endif

	if ( GetSpell "dagoth_ur_shield_02" == 1 )
		RemoveSpell "dagoth_ur_shield_02"
	endif

	if ( GetSpell "dagoth_ur_shield_03" == 1 )
		RemoveSpell "dagoth_ur_shield_03"
	endif

	if ( GetSpell "dagoth_ur_shield_04" == 1 )
		RemoveSpell "dagoth_ur_shield_04"
	endif

endif

if ( "heart_akulakhan".countHits == 1 )

	if ( GetSpell "dagoth_ur_shield_01" == 1 )
		RemoveSpell "dagoth_ur_shield_01"
	endif

	if ( GetSpell "dagoth_ur_shield_02" == 0 )
		AddSpell "dagoth_ur_shield_02"
	endif

endif

if ( "heart_akulakhan".countHits == 2 )

	if ( GetSpell "dagoth_ur_shield_02" == 1 )
		RemoveSpell "dagoth_ur_shield_02"
	endif

	if ( GetSpell "dagoth_ur_shield_03" == 0 )
		AddSpell "dagoth_ur_shield_03"
	endif

endif

if ( "heart_akulakhan".countHits == 3 )

	if ( GetSpell "dagoth_ur_shield_03" == 1 )
		RemoveSpell "dagoth_ur_shield_01"
	endif

	if ( GetSpell "dagoth_ur_shield_04" == 0 )
		AddSpell "dagoth_ur_shield_04"
	endif

endif

if ( "heart_akulakhan".countHits == 4 )

	if ( GetSpell "dagoth_ur_shield_01" == 1 )
		RemoveSpell "dagoth_ur_shield_01"
	endif

	if ( GetSpell "dagoth_ur_shield_02" == 1 )
		RemoveSpell "dagoth_ur_shield_02"
	endif

	if ( GetSpell "dagoth_ur_shield_03" == 1 )
		RemoveSpell "dagoth_ur_shield_03"
	endif

	if ( GetSpell "dagoth_ur_shield_04" == 1 )
		RemoveSpell "dagoth_ur_shield_04"
	endif

endif		

End
