begin denegorScript

short message
short journal
short death
short nolore

if ( MenuMode == 1 )
	Return
endif

if ( GetJournalIndex MS_Bouncer = 0 )
	return
endif


if ( GetHealth <= 100 )
	if ( message == 0 )
		if ( GetDeadCount Hession < 1 )
			MessageBox "Hession -- Please don't kill anyone!  Knock him out if you must, but don't kill him!"
			set message to 1
		endif
	endif
endif

if ( OnKnockout == 1 )
	if ( journal == 0 )
		SetFatigue 0
		set journal to 1
	endif
endif

if ( journal == 1 )
	if ( GetDeadCount Hession < 1 )
		Journal "MS_Bouncer" 30
		set journal to 2
	endif
endif

if ( journal >= 1 )
	if ( CellChanged == 1 )
		disable
	endif
endif

if ( OnDeath == 1 )
	if ( death == 0 )
		if ( GetDeadCount Hession < 1 )
			Journal "MS_Bouncer" 40
			set death to 1
		endif
		set hession.HessDeath to 1
	endif
endif

End
