Begin dilbornScript

short attacked
short thrudDead
short thugDead
short control
short nolore


if ( GetJournalIndex MS_BarbarianBook > 110 )
	if ( CellChanged == 1 )
		disable
		return
	endif
endif

if ( GetJournalIndex MS_BarbarianBook = 110 )
	return
endif

if ( OnDeath == 1 )
	set thrud.dilbornDead to 1
	"melur savil"->StartCombat Player
	"alam merys"->StartCombat Player
endif

if ( GetDeadCount "drathas nerus" == 1 )
	if ( GetDeadCount "melur savil" == 1 )
		if ( GetDeadCount "alam merys" == 1 )
			if ( control < 2 )
			set control to 2
			endif
		endif
	endif
endif

if ( control == 2 )

	if ( GetJournalIndex MS_BarbarianBook < 1 )
		Journal MS_BarbarianBook_B 1
		"thrud"->disable
		set control to 3
	endif

endif

if ( control == 2 )

	if ( GetDeadCount "thrud" == 1 )
		if ( GetDeadCount "dilborn" == 0 )
			Journal "MS_BarbarianBook" 140
			set control to 3
		endif
	endif

	if ( GetDeadCount "thrud" == 0 )
		if ( GetDeadCount "dilborn" == 1 )
			Journal "MS_BarbarianBook" 150
			set control to 3
		endif
	endif

	if ( GetDeadCount "thrud" == 0 )
		if ( GetDeadCount "dilborn" == 0 )
		Journal "MS_BarbarianBook" 160
		set control to 3
		endif
	endif

	if ( GetDeadCount "thrud" == 1 )
		if ( GetDeadCount "dilborn" == 1 )
		Journal "MS_BarbarianBookC" 1
		set control to 3
		endif
	endif

endif

if ( control == 3 )
	if ( GetJournalIndex MS_BarbarianBook < 1 )
		if ( CellChanged == 1 )
			disable
			set control to 4
		endif
	endif
endif

End
