Begin rignoteScript

short doOnce

if ( GetDisabled == 0 )
	if ( GetJournalIndex BM_Trial < 20 )
		Disable
	endif
elseif ( GetDisabled == 1 )
	if (GetJournalIndex BM_Trial >= 20 )
		Enable
	endif
endif

if ( GetJournalIndex BM_Trial >= 20 )
	if ( GetJournalIndex BM_Trial < 50 )
		if ( OnActivate == 1 )
			if ( doOnce == 0 )
				Activate
				Journal BM_Trial 50
				set doOnce to 1
			endif
		endif
	endif
endif






End rignoteScript
