short noLore

if ( GetJournalIndex HR_CowardDisgrace < 10 )
	return
endif

if ( GetJournalIndex HR_CowardDisgrace >= 70 )
	if ( CellChanged == 1 )
		if ( GetDisabled == 0 )
			Disable
		endif
	endif
	return
endif

if ( GetPCCell "Vivec, Arena" == 1 )

	if ( GetFlee != 30 )
		SetFlee 30
	endif

	if ( OnDeath == 1 )
		Journal HR_CowardDisgrace 70
		Set DuelActive to 0
		"Brethas Deras"->StopCombat
	endif

	if ( GetHealth < 40 )
		if ( GetItemCount "p_restore_health_s" > 0 )
			RemoveItem "p_restore_health_s" 1
			ModHealth 20
		endif
	endif

endif

End
