SCTX:
Begin ScrollSales

short doonce

if ( GetJournalIndex "MS_ScrollSales" >= 30 )
	if ( "bk_ahnia"->GetDisabled == 1 )
		"bk_ahnia"->enable
	endif
endif

if ( GetJournalIndex "MS_ScrollSales" == 30 )
	if ( "bk_ahnia"->OnActivate == 1 )
		Journal MS_ScrollSales 35
		Activate
	endif
endif

if ( doonce == 0 )
	if ( MenuMode != 1 )
		if ( GetJournalIndex "MS_ScrollSales" == 35 )
			"ahnia"->ForceGreeting
			set doonce to 1
		endif
	endif
endif

if ( "Ahnia"->OnDeath == 1 )
	Journal MS_ScrollSales 40
endif

if ( GetJournalIndex "MS_ScrollSales" == 40 )
	StopScript ScrollSales
endif


End ScrollSales
