Begin ThormoorThirsk
; Enables Thurmoor in Thirsk after the curse has been lifted.

short nolore

if ( "thormoor gray-wave"->GetHealth <=0 )
	Disable
	Return
endif

if ( "thormoor_out"->GetHealth <=0 )
	Disable
	Return
endif


if ( GetHealth > 0 )
	if ( GetJournalIndex "BM_MeadHall" >= 40 )
		if ( GetJournalIndex "BM_MeadHall" < 70 )
			Disable
		endif
	endif
endif

if ( GetHealth > 0 )
	if ( GetJournalIndex BM_CursedCaptain >= 50 )
		if ( GetJournalIndex "BM_MeadHall" >= 40 )
			if ( GetJournalIndex "BM_MeadHall" < 70 )
				Disable
			endif
		endif
	endif
endif

if ( GetHealth > 0 )
	if ( GetJournalIndex BM_CursedCaptain >= 50 )
		if ( GetJournalIndex "BM_MeadHall" >= 0 )
			if ( GetJournalIndex "BM_MeadHall" < 40 )
				Enable
			endif
		endif
	endif
endif

if ( GetJournalIndex BM_CursedCaptain >= 50 )
	if ( GetJournalIndex "BM_MeadHall" >= 70 )
		Enable
	endif
endif

if ( GetJournalIndex BM_CursedCaptain < 50 )
		Disable
	endif
 
End ThormoorThirsk
